import React, { useState } from "react";

function App() {
  const [result, setResult] = useState("");

  const register = async () => {
    const username = document.getElementById("reg-username").value;
    const password = document.getElementById("reg-password").value;

    const response = await fetch("http://localhost:3000/register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ username, password }),
    });

    const data = await response.json();
    setResult(data.message);
  };

  const login = async () => {
    const username = document.getElementById("login-username").value;
    const password = document.getElementById("login-password").value;

    const response = await fetch("http://localhost:3000/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ username, password }),
    });

    const data = await response.json();
    setResult(data.message);
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Kayıt Ol</h2>
      <input type="text" id="reg-username" placeholder="Kullanıcı Adı" />
      <input type="password" id="reg-password" placeholder="Şifre" />
      <button onClick={register}>Kayıt Ol</button>

      <h2>Giriş Yap</h2>
      <input type="text" id="login-username" placeholder="Kullanıcı Adı" />
      <input type="password" id="login-password" placeholder="Şifre" />
      <button onClick={login}>Giriş Yap</button>

      <p>{result}</p>
    </div>
  );
}

export default App;
